class FortuneApp{constructor(){this.init()}init(){this.bindEvents();this.setupTabs()}bindEvents(){document.querySelectorAll(".nav-btn").forEach(btn=>{btn.addEventListener("click",e=>{this.switchTab(e.target.dataset.tab)})});document.getElementById("startDivination")?.addEventListener("click",()=>{this.startDivination()});document.getElementById("resetDivination")?.addEventListener("click",()=>{this.resetDivination()});document.getElementById("calculateBazi")?.addEventListener("click",()=>{this.calculateBazi()})}setupTabs(){this.switchTab("xiaoliuren")}switchTab(tabName){document.querySelectorAll(".tab-content").forEach(content=>{content.classList.remove("active")});document.querySelectorAll(".nav-btn").forEach(btn=>{btn.classList.remove("active")});document.getElementById(tabName)?.classList.add("active");document.querySelector(`[data-tab="${tabName}"]`)?.classList.add("active")}startDivination(){const question=document.getElementById("question").value.trim();if(!question){alert("请输入您要询问的问题");return}document.getElementById("divinationProcess").classList.remove("hidden");setTimeout(()=>{this.performDivination(question)},2e3)}performDivination(question){const yuejiang=this.getYuejiang();const shichen=this.getShichen();const qike=this.getQike();document.getElementById("yuejiang").textContent=yuejiang;document.getElementById("shichen").textContent=shichen;document.getElementById("qike").textContent=qike;setTimeout(()=>{this.showDivinationResult(question,{yuejiang:yuejiang,shichen:shichen,qike:qike})},1e3)}getYuejiang(){const yuejiangList=["大安","留连","速喜","赤口","小吉","空亡"];return yuejiangList[Math.floor(Math.random()*yuejiangList.length)]}getShichen(){const shichenList=["子时","丑时","寅时","卯时","辰时","巳时","午时","未时","申时","酉时","戌时","亥时"];return shichenList[Math.floor(Math.random()*shichenList.length)]}getQike(){const qikeList=["青龙","朱雀","勾陈","腾蛇","白虎","玄武"];return qikeList[Math.floor(Math.random()*qikeList.length)]}showDivinationResult(question,divinationData){document.getElementById("divinationResult").classList.remove("hidden");this.requestAIAnswer({type:"xiaoliuren",prompt:`请根据小六壬卜卦结果分析问题：${question}。月将：${divinationData.yuejiang}，时辰：${divinationData.shichen}，起课：${divinationData.qike}`,targetId:"aiDivination"})}resetDivination(){document.getElementById("question").value="";document.getElementById("divinationProcess").classList.add("hidden");document.getElementById("divinationResult").classList.add("hidden")}calculateBazi(){const name=document.getElementById("baziName").value.trim();const gender=document.getElementById("baziGender").value;const year=document.getElementById("birthYear").value;const month=document.getElementById("birthMonth").value;const day=document.getElementById("birthDay").value;const hour=document.getElementById("birthHour").value;const place=document.getElementById("birthPlace").value.trim();if(!year||!month||!day||!hour){alert("请填写完整的出生信息");return}document.getElementById("baziResult").classList.remove("hidden");document.getElementById("baziNameDisplay").textContent=name||"未填写";document.getElementById("baziGenderDisplay").textContent=gender||"未选择";document.getElementById("baziBirthTime").textContent=`${year}年${month}月${day}日${this.getHourName(hour)}`;document.getElementById("baziBirthPlace").textContent=place||"未填写";this.calculateBaziPillars(year,month,day,hour);this.analyzeBaziWithAI({name:name,gender:gender,year:year,month:month,day:day,hour:hour,place:place})}getHourName(hour){const hourNames={23:"子时",1:"丑时",3:"寅时",5:"卯时",7:"辰时",9:"巳时",11:"午时",13:"未时",15:"申时",17:"酉时",19:"戌时",21:"亥时"};return hourNames[hour]||"未知时辰"}calculateBaziPillars(year,month,day,hour){const ganList=["甲","乙","丙","丁","戊","己","庚","辛","壬","癸"];const zhiList=["子","丑","寅","卯","辰","巳","午","未","申","酉","戌","亥"];document.getElementById("yearGan").textContent=ganList[Math.floor(Math.random()*ganList.length)];document.getElementById("yearZhi").textContent=zhiList[Math.floor(Math.random()*zhiList.length)];document.getElementById("monthGan").textContent=ganList[Math.floor(Math.random()*ganList.length)];document.getElementById("monthZhi").textContent=zhiList[Math.floor(Math.random()*zhiList.length)];document.getElementById("dayGan").textContent=ganList[Math.floor(Math.random()*ganList.length)];document.getElementById("dayZhi").textContent=zhiList[Math.floor(Math.random()*zhiList.length)];document.getElementById("hourGan").textContent=ganList[Math.floor(Math.random()*ganList.length)];document.getElementById("hourZhi").textContent=zhiList[Math.floor(Math.random()*zhiList.length)]}analyzeBaziWithAI(birthInfo){const prompt=`请分析以下八字信息：\n姓名：${birthInfo.name||"未填写"}\n性别：${birthInfo.gender||"未选择"}\n出生时间：${birthInfo.year}年${birthInfo.month}月${birthInfo.day}日${this.getHourName(birthInfo.hour)}\n出生地点：${birthInfo.place||"未填写"}\n\n请提供详细的八字分析，包括五行分析、十神分析、性格特征、事业财运、感情婚姻、健康运势、大运流年和综合建议。`;this.requestAIAnswer({type:"wuxing",prompt:prompt,targetId:"wuxingContent"});this.requestAIAnswer({type:"shishen",prompt:prompt,targetId:"shishenContent"});this.requestAIAnswer({type:"personality",prompt:prompt,targetId:"personalityContent"});this.requestAIAnswer({type:"career",prompt:prompt,targetId:"careerContent"});this.requestAIAnswer({type:"relationship",prompt:prompt,targetId:"relationshipContent"});this.requestAIAnswer({type:"health",prompt:prompt,targetId:"healthContent"});this.requestAIAnswer({type:"dayun",prompt:prompt,targetId:"dayunContent"});this.requestAIAnswer({type:"comprehensive",prompt:prompt,targetId:"comprehensiveContent"})}async requestAIAnswer({type:type,prompt:prompt,targetId:targetId}){const target=document.getElementById(targetId);try{if(target){target.innerHTML='<div class="loading">AI正在分析中...</div>'}const response=await fetch("/.netlify/functions/chat",{method:"POST",headers:{"Content-Type":"application/json"},body:JSON.stringify({model:"gpt-4o",messages:[{role:"system",content:"你是一位专业的命理师，精通小六壬卜卦和五行八字分析。请用专业、准确、易懂的语言为用户提供分析。"},{role:"user",content:prompt}],max_tokens:1e3})});if(!response.ok){throw new Error(`HTTP error! status: ${response.status}`)}const data=await response.json();const aiResponse=data.choices[0].message.content;if(target){target.innerHTML=`<div class="ai-response">${aiResponse}</div>`}}catch(error){console.error("AI请求失败:",error);if(target){target.innerHTML='<div class="error">AI分析暂时不可用，请稍后再试</div>'}}}}document.addEventListener("DOMContentLoaded",()=>{new FortuneApp});